// This script makes environment variables available on the client side
// through window.ENV

(function (window) {
  window.ENV = window.ENV || {};
  
  // Public environment variables
  window.ENV = {
    NEXT_PUBLIC_API_URL: 'http://localhost:8000/api',
    // Add other public environment variables here
  };
  
  // Override with production values if they exist
  try {
    const env = {
      NEXT_PUBLIC_API_URL: '%NEXT_PUBLIC_API_URL%',
      // Add other public environment variables here
    };
    
    // Replace placeholders with actual values during build
    Object.keys(env).forEach(key => {
      if (env[key] && env[key].startsWith('%') && env[key].endsWith('%')) {
        const envKey = env[key].substring(1, env[key].length - 1);
        env[key] = process.env[envKey] || '';
      }
      
      if (env[key]) {
        window.ENV[key] = env[key];
      }
    });
  } catch (e) {
    console.error('Error loading environment variables:', e);
  }
})(window);
